SET client_encoding TO 'UTF8';

	
	-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Obter valor total comprometido <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--
	/*
	Objetivo: Retornar o valor comprometido do contrato passado por parametro.
	Parametros: 
	nr_seq_contrato_p = Numero de sequencia do contrato.
	ie_atual_p        = Quando 'S': Ira verificar o valor atual de ordens de compra vinculadas ao contrato.
					    Quando 'N': Ira verificar o valor de acordo com o ultimo gerenciamento de saldo do contrato existente na tabela "contrato_gerenciamento".
	*/
CREATE OR REPLACE FUNCTION gerenciamento_contrato_pck.obter_valor_comprometido (nr_seq_contrato_p bigint, ie_atual_p text) RETURNS bigint AS $body$
DECLARE

	
	vl_comprometido_contrato_w	double precision;
				
		
	
BEGIN
	
	vl_comprometido_contrato_w := 0;
	
	if (gerenciamento_contrato_pck.obter_tipo_contrato(nr_seq_contrato_p) = 'P') then
	
		if (ie_atual_p = 'N') then
			begin
				select a.vl_comprometido
				  into STRICT vl_comprometido_contrato_w
				  from contrato_gerenciamento a
				 where a.nr_seq_contrato = nr_seq_contrato_p
				   and a.dt_atualizacao = (SELECT max(b.dt_atualizacao)
											 from contrato_gerenciamento b
											where b.nr_seq_contrato = a.nr_seq_contrato);
			end;
		else
			begin
				/* Valor comprometido em solicitacoes de compra */

				vl_comprometido_contrato_w := gerenciamento_contrato_pck.obter_qt_vl_item_solic(nr_seq_contrato_p, 'VL','S');
						
				/* Valor comprometido em ordens de compra */
		
				vl_comprometido_contrato_w := vl_comprometido_contrato_w + gerenciamento_contrato_pck.obter_qt_vl_item_ordens(nr_seq_contrato_p, 'VL', 'S');
			end;
		end if;
	
	end if;
	
	return	vl_comprometido_contrato_w;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
