	-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Obter valor total realizado <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--
	/*
	Objetivo: Retornar o valor realizado do contrato passado por parametro.
			  Este se trata dos valores em notas fiscais e titulos a pagar vinculados ao contrato.
	Parametros: 
	nr_seq_contrato_p = Numero de sequencia do contrato.
	ie_atual_p        = Quando 'S': Ira calcular o valor atual comprometido do contrato.
					    Quando 'N': Ira retornar o valor de acordo com o ultimo gerenciamento de saldo do contrato existente na tabela "contrato_gerenciamento".
	*/
CREATE OR REPLACE FUNCTION gerenciamento_contrato_pck.obter_valor_realizado (nr_seq_contrato_p bigint, ie_atual_p text) RETURNS bigint AS $body$
DECLARE

	
	vl_realizado_contrato_w		contrato_gerenciamento.vl_realizado%type;
	
		
	
BEGIN
	
	vl_realizado_contrato_w := 0;
	
	if (ie_atual_p = 'N') then
		begin
			select a.vl_realizado
			  into STRICT vl_realizado_contrato_w
			  from contrato_gerenciamento a
			 where a.nr_seq_contrato = nr_seq_contrato_p
			   and a.dt_atualizacao = (SELECT max(b.dt_atualizacao)
										 from contrato_gerenciamento b
										where b.nr_seq_contrato = a.nr_seq_contrato);
		end;
	else
	
		vl_realizado_contrato_w := vl_realizado_contrato_w + gerenciamento_contrato_pck.obter_qt_vl_item_notas(nr_seq_contrato_p, 'VL', 'S');
		
		if (gerenciamento_contrato_pck.obter_tipo_contrato(nr_seq_contrato_p) = 'P') then
			vl_realizado_contrato_w := vl_realizado_contrato_w + gerenciamento_contrato_pck.obter_qt_vl_titulos_pagar(nr_seq_contrato_p, 'VL', 'S');
		else
			vl_realizado_contrato_w := vl_realizado_contrato_w + gerenciamento_contrato_pck.obter_qt_vl_titulos_receber(nr_seq_contrato_p, 'VL', 'S');
		end if;
	
	end if;
	
	return	vl_realizado_contrato_w;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
