	-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Obter valor total do contrato <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--
	/*
	Objetivo: Retornar o valor total do contrato passado por parametro.
	Parametros: 
	nr_seq_contrato_p = Numero de sequencia do contrato.
	ie_atual_p        = Quando 'S': Ira verificar o valor total atual do contrato.
					    Quando 'N': Ira retornar o valor de acordo com o ultimo gerenciamento de saldo do contrato existente na tabela "contrato_gerenciamento".
	*/
CREATE OR REPLACE FUNCTION gerenciamento_contrato_pck.obter_valor_total (nr_seq_contrato_p bigint, ie_atual_p text) RETURNS bigint AS $body$
DECLARE

		
		valor_total_contrato_w  contrato.vl_total_contrato%type;
		
		
BEGIN
		
		valor_total_contrato_w := 0;
		
		if (ie_atual_p = 'N') then
			begin
				select a.vl_total
				  into STRICT valor_total_contrato_w
				  from contrato_gerenciamento a
				 where a.nr_seq_contrato = nr_seq_contrato_p
				   and a.dt_atualizacao = (SELECT max(b.dt_atualizacao)
											 from contrato_gerenciamento b
											where b.nr_seq_contrato = a.nr_seq_contrato);
			end;
		else
			select vl_total_contrato
			  into STRICT valor_total_contrato_w
		      from contrato
		     where nr_sequencia = nr_seq_contrato_p;
		
		end if;
		
		return valor_total_contrato_w;
		
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
