SET client_encoding TO 'UTF8';

	
	-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Valor consumido pelo fornecedor em titulos a pagar  <<<<<<<<<<<<<<<<<<<<<<<<<<<--	
	/*
	Objetivo: Retornar o valor consumido pelo fornecedor em titulos a pagar vinculados ao contrato.
	Parametros: 
	nr_seq_contrato_p   = Numero de sequencia do contrato.
	cd_cgc_fornecedor_p = CNPJ do fornecedor.
	*/
CREATE OR REPLACE FUNCTION gerenciamento_contrato_pck.obter_vl_tit_pagar_fornec (nr_seq_contrato_p bigint, cd_cgc_fornecedor_p bigint) RETURNS bigint AS $body$
DECLARE

		
	vl_titulo_pagar_w	contrato_gerenciamento.vl_titulo_pagar%type;
	qt_titulo_pagar_w   contrato_gerenciamento.qt_titulo_pagar%type;
	cd_moeda_contrato_w moeda.cd_moeda%type;

	c01 CURSOR(nr_seq_contrato_c bigint, cd_cgc_fornecedor_c bigint) FOR
		SELECT nr_titulo,
			   cd_moeda
		 from titulo_pagar
		where nr_seq_contrato = nr_seq_contrato_c
		  and coalesce(cd_pessoa_fisica, cd_cgc) = cd_cgc_fornecedor_c
		  and ie_situacao in ('A','L','T','B')
		  and	coalesce(nr_seq_nota_fiscal::text, '') = '';
	
	
BEGIN
	vl_titulo_pagar_w   := 0;	
	qt_titulo_pagar_w   := 0;
	cd_moeda_contrato_w := gerenciamento_contrato_pck.obter_moeda_contrato(nr_seq_contrato_p);
	
	For r01 in c01(nr_seq_contrato_p, cd_cgc_fornecedor_p) loop
	
		vl_titulo_pagar_w := vl_titulo_pagar_w + gerenciamento_contrato_pck.obter_valor_moeda_convertido(nr_seq_contrato_p, cd_moeda_contrato_w, r01.cd_moeda, gerenciamento_contrato_pck.obter_vl_titulo_pagar(r01.nr_titulo));
		
	end loop;

	return vl_titulo_pagar_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
