	-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Obter valor total do titulo a receber <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--	
	/*
	Objetivo: Retornar o valor total do titulo a pagar.
	Parametros: 
	nr_seq_tit_rec_p = Numero de sequencia do titulo a receber.
	*/
CREATE OR REPLACE FUNCTION gerenciamento_contrato_pck.obter_vl_titulo_receber (nr_seq_tit_rec_p bigint) RETURNS bigint AS $body$
DECLARE

					
	vl_titulo_w	titulo_receber.vl_titulo%type;	
	
	
BEGIN
	
	select coalesce(max(vl_titulo), 0)
	  into STRICT	vl_titulo_w
	  from 	titulo_receber
	 where nr_titulo = nr_seq_tit_rec_p;
	
	return vl_titulo_w;
	
	END;

$body$
