CREATE OR REPLACE FUNCTION guias_nom_pck.monta_linha ( ds_texto_p text, nr_linha_p bigint, ie_separador_p text) RETURNS varchar AS $body$
DECLARE


ds_registro_w   varchar(4000);
lista_w		lista_varchar_pck.tabela_varchar;

BEGIN

    lista_w	:= obter_lista_string2(ds_texto_p, ie_separador_p);

    for i in lista_w.first..lista_w.last loop
      if length(trim(both ds_registro_w)) > 0 then
        ds_registro_w	:= trim(both ds_registro_w) ||'|'|| trim(both lista_w(i));
      else
        ds_registro_w	:= trim(both lista_w(i));
      end if;
    end loop;

return ds_registro_w;

END;

