CREATE OR REPLACE FUNCTION hdm_indic_pck.get_clinic_ant (si_state_p text, nm_disease_p text) RETURNS bigint AS $body$
DECLARE

		nr_seq_clinic_ant_w	bigint;
	c_diseases CURSOR FOR
		SELECT	a.nr_sequencia
		from	hdm_indic_dm_clinic_ant a
		where	a.nm_disease = nm_disease_p
		and	a.si_state = si_state_p;
	
BEGIN
		for r_c_diseases in c_diseases loop
			nr_seq_clinic_ant_w := r_c_diseases.nr_sequencia;
		end loop;
		return coalesce(nr_seq_clinic_ant_w,0);
	END;

$body$
