SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION hdm_indic_pck.get_cost_type (nr_seq_segurado_p bigint) RETURNS bigint AS $body$
DECLARE

	nr_seq_cost_type_w	bigint;
	c_cost_type CURSOR FOR
		SELECT	nr_sequencia
		from	hdm_indic_dim_cost_type
		where	si_hdm = (	SELECT	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
					from	pls_conta a,
						mprev_config_geral b
					where	a.nr_seq_segurado = nr_seq_segurado_p
					and	a.nr_seq_prestador_exec = b.nr_seq_prestador_exec);
	
BEGIN
		for r_c_cost_type in c_cost_type loop
			nr_seq_cost_type_w  := r_c_cost_type.nr_sequencia;
		end loop;
		
		return	nr_seq_cost_type_w;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
