CREATE OR REPLACE FUNCTION hdm_indic_pck.get_ds_day ( nr_day_p bigint) RETURNS varchar AS $body$
DECLARE

		nr_seq_message_w	bigint;
	
BEGIN
	case	nr_day_p
		when 2 then	
			nr_seq_message_w := 357979;
		when 3 then
			nr_seq_message_w := 357980;
		when 4 then
			nr_seq_message_w := 357981;
		when 5 then	
			nr_seq_message_w := 357982;
		when 6 then
			nr_seq_message_w := 357983;
		when 7 then
			nr_seq_message_w := 357984;
		when 1 then
			nr_seq_message_w := 357985;
		else
			return	'';
	end case;
	return wheb_mensagem_pck.get_texto(nr_seq_message_w);
	END;

