CREATE OR REPLACE FUNCTION hdm_indic_pck.get_medication (si_registrated_p text, nm_medication_p text) RETURNS bigint AS $body$
DECLARE

		nr_seq_medication_w	bigint;
	c_medications CURSOR FOR
		SELECT	a.nr_sequencia
		from	hdm_indic_dm_medication a
		where	a.nm_medication = nm_medication_p
		and	a.si_registrated = si_registrated_p;
	
BEGIN
		for r_c_medications in c_medications loop
			nr_seq_medication_w := r_c_medications.nr_sequencia;
		end loop;
		return coalesce(nr_seq_medication_w,0);
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
