CREATE OR REPLACE FUNCTION hdm_indic_pck.get_reference ( qt_result_p bigint, qt_min_p bigint, qt_max_p bigint) RETURNS bigint AS $body$
DECLARE

		si_reference_w		varchar(1) := ' ';
		nr_seq_reference_w	bigint;
	
BEGIN
		if (qt_result_p between qt_min_p and qt_max_p) then
			si_reference_w := '=';
		elsif (qt_result_p > qt_max_p) then
			si_reference_w := '>';
