			si_reference_w := '<';
		end if;
		
		select	a.nr_sequencia
		into STRICT	nr_seq_reference_w
		from	hdm_indic_dm_reference a
		where	a.si_reference = si_reference_w;
		
		return nr_seq_reference_w;
	END;

