CREATE OR REPLACE FUNCTION hdm_indic_pck.get_susp_detail (si_status_p text, ds_reason_p text, si_reason_kind_p text) RETURNS bigint AS $body$
DECLARE

	nr_seq_susp_detail_w	bigint;
	c_detail CURSOR FOR
		SELECT	x.nr_sequencia
		from	hdm_indic_dm_susp_details x
		where	x.si_status = si_status_p
		and	x.ds_reason = ds_reason_p
		and	x.si_reason_kind = si_reason_kind_p;
	
BEGIN
		for r_c_detail in c_detail loop
			nr_seq_susp_detail_w := r_c_detail.nr_sequencia;
		end loop;
		
		return nr_seq_susp_detail_w;
	END;

$body$
