CREATE OR REPLACE FUNCTION hdm_indic_pck.get_vaccine (nm_vaccine_p text, nm_vaccine_dose_p text) RETURNS bigint AS $body$
DECLARE

	nr_seq_vaccine_w	bigint;
	c_vaccine CURSOR FOR
		SELECT	nr_sequencia
		from	hdm_indic_dm_vaccine
		where	nm_vaccine = nm_vaccine_p
		and	nm_vaccine_dose = nm_vaccine_p || ' (' || nm_vaccine_dose_p || ')';
	
