CREATE OR REPLACE PROCEDURE hdm_indic_pck.link_activ_group_with_mu_fact () AS $body$
BEGIN
		merge into hdm_indic_ft_medication_ag y
		using(	SELECT	a.nr_sequencia nr_seq_dimension,
				x.nr_sequencia nr_seq_fact
			FROM hdm_indic_dm_day y, mprev_participante d, mprev_grupo_turma_partic c, hdm_indic_dm_activ_group a, hdm_indic_ft_medication x
LEFT OUTER JOIN hdm_indic_dm_day z ON (x.nr_seq_day_end = z.nr_sequencia)
WHERE y.dt_complete_date <= coalesce(c.dt_saida,y.dt_complete_date) and (z.dt_complete_date > c.dt_saida or coalesce(z.dt_complete_date::text, '') = '') and a.nr_seq_turma = c.nr_seq_turma and c.nr_seq_participante = d.nr_sequencia and y.nr_sequencia = x.nr_seq_day_start  and d.cd_pessoa_fisica= x.nr_dif_person group by
				a.nr_sequencia,
				x.nr_sequencia
			) x
		on (	y.nr_seq_fact = x.nr_seq_fact and
			y.nr_seq_dimension = x.nr_seq_dimension)
		when not matched then
			insert(y.nr_seq_fact,
				y.nr_seq_dimension)
			values (x.nr_seq_fact,
				x.nr_seq_dimension);
		commit;

		merge into hdm_indic_ft_medication_ag y
		using(	SELECT	0 nr_seq_dimension,
				x.nr_sequencia nr_seq_fact
			from	hdm_indic_ft_medication x
			where	not exists (select	1
						from	hdm_indic_ft_medication_ag a
						where	a.nr_seq_fact	= x.nr_sequencia)
			) x
		on (	y.nr_seq_fact = x.nr_seq_fact and
			y.nr_seq_dimension = x.nr_seq_dimension)
		when not matched then
			insert(y.nr_seq_fact,
				y.nr_seq_dimension)
			values (x.nr_seq_fact,
				x.nr_seq_dimension);
		commit;
	END;

