CREATE OR REPLACE PROCEDURE hdm_indic_pck.link_campaign_with_co_fact () AS $body$
BEGIN
		merge into hdm_indic_ft_cost_cp y
		using(	SELECT	a.nr_sequencia nr_seq_dimension,
				x.nr_sequencia nr_seq_fact,
				x.vl_cost,
				x.pr_risk
			from	hdm_indic_dm_campaign a,
				mprev_campanha_partic c,
				mprev_participante d,
				hdm_indic_ft_cost x,
				hdm_indic_dm_month y
			where	y.dt_complete_date between pkg_date_utils.start_of(c.dt_inclusao,'MONTH', 0) and pkg_date_utils.start_of(coalesce(c.dt_exclusao,y.dt_complete_date),'MONTH', 0)
			and	a.nr_seq_campaign = c.nr_seq_campanha
			and	c.nr_seq_participante = d.nr_sequencia
			and	d.cd_pessoa_fisica = x.nr_dif_person
			and	x.nr_seq_month	= y.nr_sequencia
			group by
				a.nr_sequencia,
				x.nr_sequencia,
				x.vl_cost,
				x.pr_risk
			) x
		on (	y.nr_seq_fact = x.nr_seq_fact and
			y.nr_seq_dimension = x.nr_seq_dimension)
		when not matched then
			insert(y.nr_seq_fact,
				y.nr_seq_dimension,
				y.vl_cost,
				y.pr_risk)
			values (x.nr_seq_fact,
				x.nr_seq_dimension,
				x.vl_cost,
				x.pr_risk);
		commit;
		
		merge into hdm_indic_ft_cost_cp y
		using(	SELECT	0 nr_seq_dimension,
				x.nr_sequencia nr_seq_fact,
				x.vl_cost,
				x.pr_risk
			from	hdm_indic_ft_cost x
			where	not exists (select	1
						from	hdm_indic_ft_cost_cp a
						where	a.nr_seq_fact	= x.nr_sequencia)
			) x
		on (	y.nr_seq_fact = x.nr_seq_fact and
			y.nr_seq_dimension = x.nr_seq_dimension)
		when not matched then
			insert(y.nr_seq_fact,
				y.nr_seq_dimension,
				y.vl_cost,
				y.pr_risk)
			values (x.nr_seq_fact,
				x.nr_seq_dimension,
				x.vl_cost,
				x.pr_risk);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
