CREATE OR REPLACE PROCEDURE hdm_indic_pck.link_diagnosis_whit_pr_fact () AS $body$
BEGIN
		merge into hdm_indic_ft_procedure_dg y
		using(	SELECT	a.nr_sequencia nr_seq_dimension,
				x.nr_sequencia nr_seq_fact
			from	hdm_indic_dm_bill_diag a,
				pls_diagnostico_conta b,
				pls_segurado d,
				pls_conta c,
				hdm_indic_ft_procedure x,
				hdm_indic_dm_month y
			where	y.dt_complete_date = pkg_date_utils.start_of(c.dt_atendimento_referencia, 'MONTH', 0)
			and	a.cd_cid = b.cd_doenca
			and	c.nr_sequencia = b.nr_seq_conta
			and	d.nr_sequencia = c.nr_seq_segurado
			and	d.cd_pessoa_fisica = x.nr_dif_person
			and	x.nr_seq_month	= y.nr_sequencia
			group by
				a.nr_sequencia,
				x.nr_sequencia
			) x
		on (	y.nr_seq_fact = x.nr_seq_fact and
			y.nr_seq_dimension = x.nr_seq_dimension)
		when not matched then
			insert(y.nr_seq_fact,
				y.nr_seq_dimension)
			values (x.nr_seq_fact,
				x.nr_seq_dimension);
		commit;
		
		merge into hdm_indic_ft_procedure_dg y
		using(	SELECT	0 nr_seq_dimension,
				x.nr_sequencia nr_seq_fact
			from	hdm_indic_ft_procedure x
			where	not exists (select	1
						from	hdm_indic_ft_procedure_dg a
						where	a.nr_seq_fact	= x.nr_sequencia)
			) x
		on (	y.nr_seq_fact = x.nr_seq_fact and
			y.nr_seq_dimension = x.nr_seq_dimension)
		when not matched then
			insert(y.nr_seq_fact,
				y.nr_seq_dimension)
			values (x.nr_seq_fact,
				x.nr_seq_dimension);
		commit;
	END;

$body$
