SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.link_program_with_pt_fact () AS $body$
BEGIN
		merge into hdm_indic_ft_partic_pr y
		using(	/* Program whit module */

			SELECT	a.nr_sequencia nr_seq_dimension,
				x.nr_sequencia nr_seq_fact
			from	hdm_indic_dm_program a,
				mprev_programa_modulo pm,
				mprev_prog_partic_modulo d,
				mprev_programa_partic e,
				mprev_participante g,
				hdm_indic_ft_partic x,
				hdm_indic_dm_month y
			where	y.dt_complete_date between pkg_date_utils.start_of(e.dt_inclusao,'MONTH', 0) and pkg_date_utils.start_of(coalesce(e.dt_exclusao,y.dt_complete_date),'MONTH', 0)
			and	y.dt_complete_date between pkg_date_utils.start_of(d.dt_entrada,'MONTH', 0) and pkg_date_utils.start_of(coalesce(d.dt_saida,y.dt_complete_date),'MONTH', 0)
			and	a.nr_seq_program = pm.nr_seq_programa
			and	a.nr_seq_module = pm.nr_seq_modulo
			and	d.nr_seq_prog_modulo = pm.nr_sequencia
			and	d.nr_seq_programa_partic = e.nr_sequencia
			and	g.nr_sequencia	= e.nr_seq_participante
			and	x.nr_dif_person	= g.cd_pessoa_fisica
			and	y.nr_sequencia = x.nr_seq_month
			group by
				a.nr_sequencia,
				x.nr_sequencia
			having	count(1) >= 1
			
union

			/* Without module */


			SELECT	distinct
				a.nr_sequencia nr_seq_dimension,
				x.nr_sequencia nr_seq_fact
			from	hdm_indic_dm_program a,
				mprev_programa_partic e,
				mprev_participante g,
				hdm_indic_ft_partic x,
				hdm_indic_dm_month y
			where	y.dt_complete_date between pkg_date_utils.start_of(e.dt_inclusao,'MONTH', 0) and pkg_date_utils.start_of(coalesce(e.dt_exclusao,y.dt_complete_date),'MONTH', 0)
			and	a.nr_seq_program = e.nr_seq_programa
			and	a.nr_seq_module = 0
			and	g.nr_sequencia	= e.nr_seq_participante
			and	x.nr_dif_person	= g.cd_pessoa_fisica
			and	y.nr_sequencia = x.nr_seq_month
			group by
				a.nr_sequencia,
				x.nr_sequencia
			having	count(1) >= 1
			) x
		on (	y.nr_seq_fact = x.nr_seq_fact and
			y.nr_seq_dimension = x.nr_seq_dimension)
		when not matched then
			insert(y.nr_seq_fact,
				y.nr_seq_dimension)
			values (x.nr_seq_fact,
				x.nr_seq_dimension);
		commit;

		merge into hdm_indic_ft_partic_pr y
		using(	SELECT	0 nr_seq_dimension,
				x.nr_sequencia nr_seq_fact
			from	hdm_indic_ft_partic x
			where	not exists (select	1
						from	hdm_indic_ft_partic_pr a
						where	a.nr_seq_fact	= x.nr_sequencia)
			) x
		on (	y.nr_seq_fact = x.nr_seq_fact and
			y.nr_seq_dimension = x.nr_seq_dimension)
		when not matched then
			insert(y.nr_seq_fact,
				y.nr_seq_dimension)
			values (x.nr_seq_fact,
				x.nr_seq_dimension);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hdm_indic_pck.link_program_with_pt_fact () FROM PUBLIC;
