CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_bill_diag () AS $body$
BEGIN
		merge into hdm_indic_dm_bill_diag y
		using(	SELECT	cd_cid,
				ds_disease,
				si_without_diag
			from (	select	a.cd_doenca_cid cd_cid,
						a.ds_doenca_cid ds_disease,
						'N' si_without_diag
					from	cid_doenca_v a
					
union

					SELECT	null cd_cid,
						wheb_mensagem_pck.get_texto(365921) ds_disease,
						'S' si_without_diag 
					
				) alias1 
			) x
		on (	(y.cd_cid = x.cd_cid and
			y.ds_disease = x.ds_disease) or (y.nr_sequencia = 0))
		when not matched then
			insert(y.nr_sequencia, 
				y.dt_atualizacao, 
				y.nm_usuario, 
				y.dt_atualizacao_nrec, 
				y.nm_usuario_nrec, 
				y.cd_cid, 
				y.ds_disease)
			values (CASE WHEN x.si_without_diag='S' THEN  0  ELSE nextval('hdm_indic_dm_bill_diag_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.cd_cid,
				x.ds_disease);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
