CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_bill_type () AS $body$
BEGIN
		merge into hdm_indic_dm_bill_type y
		using(	SELECT	'I' si_type,
				wheb_mensagem_pck.get_texto(371649) nm_type,
				'N' si_without_type
			
			
union

			SELECT	'U' si_type,
				wheb_mensagem_pck.get_texto(371650) nm_type,
				'N' si_without_type 
			
			
union

			select	'X' si_type,
				wheb_mensagem_pck.get_texto(371621) nm_type,
				'S' si_without_type
			
			) x
		on (	(y.si_type = x.si_type and
			y.nm_type = x.nm_type) or (y.nr_sequencia = 0))
		when not matched then
			insert(y.nr_sequencia, 
				y.dt_atualizacao, 
				y.nm_usuario, 
				y.dt_atualizacao_nrec, 
				y.nm_usuario_nrec, 
				y.si_type, 
				y.nm_type)
			values (CASE WHEN x.si_without_type='S' THEN  0  ELSE nextval('hdm_indic_dm_bill_type_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.si_type,
				x.nm_type);
		commit;
	END;

$body$
