SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_bp_range () AS $body$
DECLARE

	nr_range_order_w	bigint := 0;
	last_nr_age_minimum_w	hdm_indic_glycemia_range.nr_age_minimum%type	:= -1;
	last_si_sex_w		hdm_indic_glycemia_range.si_sex%type		:= 'X';
	c_indic_dm_bp_range CURSOR FOR
		SELECT	nr_sequencia,
			1 si_order,
			nr_minimum,
			nr_age_minimum,
			nr_age_maximum,
			si_sex
		from	hdm_indic_dm_bp_range
		order by
			si_order,
			nr_age_minimum,
			si_sex,
			nr_minimum;
	
BEGIN
		merge into hdm_indic_dm_bp_range y
		using(	SELECT	a.nm_range || CHR(13) || CHR(10) || '(' || a.qt_bp_start || ' ~ ' || a.qt_bp_end || ')' nm_range,
				a.qt_bp_start nr_minimum,
				a.qt_bp_end nr_maximum,
				a.nr_age_minimum nr_age_minimum, 
				a.nr_age_maximum nr_age_maximum, 
				a.si_sex si_sex,
				'N' si_without_range
			from 	hdm_indic_blood_pres_range a
			where	a.ie_situacao = 'A'
			
union all

			/* Without range*/


			SELECT	wheb_mensagem_pck.get_texto(358141) nm_range,
				null nr_minimum,
				null nr_maximum,
				null nr_age_minimum,
				null nr_age_maximum,
				null si_sex,
				'S' si_without_range
			
			) x
		on (	y.nm_range = x.nm_range and (y.nr_minimum = x.nr_minimum or (coalesce(y.nr_minimum::text, '') = '' and coalesce(x.nr_minimum::text, '') = '')) and (y.nr_maximum = x.nr_maximum or (coalesce(y.nr_maximum::text, '') = '' and coalesce(x.nr_maximum::text, '') = '')) and (y.nr_age_maximum = x.nr_age_maximum or (coalesce(y.nr_age_maximum::text, '') = '' and coalesce(x.nr_age_maximum::text, '') = '')) and (y.nr_age_minimum = x.nr_age_minimum or (coalesce(y.nr_age_minimum::text, '') = '' and coalesce(x.nr_age_minimum::text, '') = '')) and (y.si_sex = x.si_sex or (coalesce(y.si_sex::text, '') = '' and coalesce(x.si_sex::text, '') = '')))
		when not matched then
			insert(y.nr_sequencia,
				y.dt_atualizacao,
				y.nm_usuario,
				y.dt_atualizacao_nrec,
				y.nm_usuario_nrec,
				y.nm_range,
				y.nr_minimum,
				y.nr_maximum,
				y.nr_age_minimum, 
				y.nr_age_maximum, 
				y.si_sex)
			values (CASE WHEN x.si_without_range='S' THEN  0  ELSE nextval('hdm_indic_dm_bp_range_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.nm_range,
				x.nr_minimum,
				x.nr_maximum,
				x.nr_age_maximum,
				x.nr_age_minimum,
				x.si_sex);
		commit;
		
		for r_c_indic_dm_bp_range in c_indic_dm_bp_range loop
			update	hdm_indic_dm_bp_range
			set	nr_range_order = nr_range_order_w
			where	nr_sequencia = r_c_indic_dm_bp_range.nr_sequencia;
			
			if (last_nr_age_minimum_w != r_c_indic_dm_bp_range.nr_age_maximum or
				 last_si_sex_w != r_c_indic_dm_bp_range.si_sex) then
				nr_range_order_w := 1;
			else
				nr_range_order_w := nr_range_order_w + 1;
			end if;
			last_nr_age_minimum_w := r_c_indic_dm_bp_range.nr_age_maximum;
			last_si_sex_w := r_c_indic_dm_bp_range.si_sex;
		end loop;
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hdm_indic_pck.populate_dim_bp_range () FROM PUBLIC;
