SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_campaign () AS $body$
BEGIN
		merge into hdm_indic_dm_campaign y
		using(	SELECT	a.nr_sequencia nr_seq_campaign,
				a.nm_campanha nm_campaign,
				'N' si_without_campaing
			from	mprev_campanha a
			/* Without campaign */


			
union all

			SELECT	0 nr_seq_campaign,
				wheb_mensagem_pck.get_texto(358216) nm_campaign,
				'S' si_without_campaing
			) x
		on (	(y.nr_seq_campaign = x.nr_seq_campaign) or (y.nr_sequencia = 0))
		when not matched then
			insert(nr_sequencia,
				dt_atualizacao, 
				nm_usuario, 
				dt_atualizacao_nrec, 
				nm_usuario_nrec, 
				nm_campaign,
				nr_seq_campaign)
			values (CASE WHEN x.si_without_campaing='S' THEN  0   ELSE nextval('hdm_indic_dm_campaign_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.nm_campaign,
				x.nr_seq_campaign);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hdm_indic_pck.populate_dim_campaign () FROM PUBLIC;
