SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_clinic_ant () AS $body$
BEGIN
		merge into hdm_indic_dm_clinic_ant y
		using(	SELECT	dm.nm_disease,
				dm.si_state,
				dm.ds_state,
				dm.si_without_disease
			from (
				-- With all fields

				SELECT	coalesce(obter_desc_cid_doenca(a.cd_doenca),coalesce(obter_desc_pe_doenca(a.nr_seq_doenca),a.ds_doenca)) nm_disease,
					coalesce(a.ie_status,'0') si_state,
					coalesce(obter_valor_dominio(1332, a.ie_status),wheb_mensagem_pck.get_texto(366272)) ds_state,
					'N' si_without_disease
				from 	paciente_antec_clinico a
				where	coalesce(obter_desc_cid_doenca(a.cd_doenca),coalesce(obter_desc_pe_doenca(a.nr_seq_doenca),a.ds_doenca))  is not null
				
union all

				-- Without disease

				select	wheb_mensagem_pck.get_texto(365921) nm_disease,
					coalesce(a.ie_status,'0') si_siate,
					coalesce(obter_valor_dominio(1332, a.ie_status),wheb_mensagem_pck.get_texto(366272)) ds_state,
					'N' si_without_disease
				from	paciente_antec_clinico a
				
union all

				-- Without disease and state

				select	wheb_mensagem_pck.get_texto(365921) nm_disease,
					'0' si_state,
					wheb_mensagem_pck.get_texto(366272) ds_state,
					'S' si_without_disease
				
			) dm
			group by	dm.nm_disease,
					dm.si_state,
					dm.ds_state,
					dm.si_without_disease
			) x
		on (	(y.nm_disease = x.nm_disease and
			y.si_state = x.si_state and
			y.ds_state = x.ds_state) or (y.nr_sequencia = 0))
		when not matched then
			insert(y.nr_sequencia, 
				y.dt_atualizacao, 
				y.nm_usuario, 
				y.dt_atualizacao_nrec, 
				y.nm_usuario_nrec, 
				y.nm_disease, 
				y.si_state,
				y.ds_state)
			values (CASE WHEN x.si_without_disease='S' THEN  0  ELSE nextval('hdm_indic_dm_clinic_ant_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.nm_disease,
				x.si_state,
				x.ds_state);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hdm_indic_pck.populate_dim_clinic_ant () FROM PUBLIC;
