SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_control_group () AS $body$
BEGIN
		merge into hdm_indic_dm_control_group y
		using(	SELECT	a.nm_grupo nm_group,
				'N' si_without_group
			from	mprev_grupo_controle a
			where	a.ie_situacao = 'A'
			
union

			SELECT	wheb_mensagem_pck.get_texto(358217) nm_group,
				'S' si_without_group
			
			) x
		on ( 	(y.nm_group = x.nm_group) or (y.nr_sequencia = 0))
		when not matched then
			insert(y.nr_sequencia,
				y.dt_atualizacao, 
				y.nm_usuario, 
				y.dt_atualizacao_nrec, 
				y.nm_usuario_nrec, 
				y.nm_group)
			values (CASE WHEN x.si_without_group='S' THEN  0  ELSE nextval('hdm_indic_dm_control_group_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.nm_group);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hdm_indic_pck.populate_dim_control_group () FROM PUBLIC;
