SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_exams () AS $body$
BEGIN
		merge into hdm_indic_dm_exam y
		using(	-- With all fields
			SELECT	a.nr_seq_exame nr_seq_exam,
				a.nm_exame nm_exam,
				b.ds_grupo_exame_lab nm_group,
				'N' si_default
			from	grupo_exame_lab b,
				exame_laboratorio a
			where	a.nr_seq_grupo = b.nr_sequencia
			
union all

			-- Without group 

			SELECT	a.nr_seq_exame nr_seq_exam,
				a.nm_exame nm_exam,
				wheb_mensagem_pck.get_texto(358217) nm_group,
				'N' si_default
			from	exame_laboratorio a
			where	coalesce(a.nr_seq_grupo::text, '') = ''
			
union all

			-- Default

			select	0 nr_seq_exam,
				wheb_mensagem_pck.get_texto(358141) nm_exam,
				wheb_mensagem_pck.get_texto(358217) nm_group,
				'S' si_default
			
			) x
		on (	y.nr_seq_exam = x.nr_seq_exam and
			y.nm_exam = x.nm_exam and
			y.nm_group = x.nm_group)
		when not matched then
			insert(y.nr_sequencia, 
				y.dt_atualizacao, 
				y.nm_usuario, 
				y.dt_atualizacao_nrec, 
				y.nm_usuario_nrec, 
				y.nr_seq_exam,
				y.nm_exam, 
				y.nm_group)
			values (CASE WHEN x.si_default='S' THEN  0  ELSE nextval('hdm_indic_dm_exam_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.nr_seq_exam,
				x.nm_exam,
				x.nm_group);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
