SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_habit () AS $body$
BEGIN
		merge into hdm_indic_dm_habit y
		using (	SELECT	a.si_habit,				
				a.nm_habit,
				b.si_frequency,
				b.ds_frequency,
				c.si_exists,
				a.si_without_habit
			from (select	a.vl_dominio si_habit,
					a.ds_valor_dominio nm_habit,
					'N' si_without_habit
				from 	valor_dominio_v a
				where	a.ie_situacao = 'A'
				and	a.cd_dominio = 1335) a,
				(-- With all fields
				SELECT	vl_dominio si_frequency,
					ds_valor_dominio ds_frequency
				from	valor_dominio_v
				where	cd_dominio = 1336
				-- Without frequency

				
union all

				select	'0' si_frequency,
					wheb_mensagem_pck.get_texto(363155) ds_frequency
				) b,
				(select	'S' si_exists
				
				
union alL

				select	'N' si_exists
				) c
			
union

			select	null,
				wheb_mensagem_pck.get_texto(361270),
				'0',
				wheb_mensagem_pck.get_texto(363155),
				'N',
				'S'
			
			) x
		on (	(y.nm_habit = x.nm_habit and 
			y.si_frequency = x.si_frequency and
			y.si_habit = x.si_habit and
			y.si_exists = x.si_exists) or (y.nr_sequencia = 0))
		when not matched then
			insert(y.nr_sequencia, 
				y.dt_atualizacao, 
				y.nm_usuario, 
				y.dt_atualizacao_nrec, 
				y.nm_usuario_nrec, 
				y.nm_habit, 
				y.si_frequency,
				y.ds_frequency,
				y.si_habit,
				y.si_exists)
			values (CASE WHEN x.si_without_habit='S' THEN  0  ELSE nextval('hdm_indic_dm_habit_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.nm_habit,
				x.si_frequency,
				x.ds_frequency,
				x.si_habit,
				x.si_exists);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
