SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_medication () AS $body$
BEGIN
		merge into hdm_indic_dm_medication y
		using( SELECT	dm.nm_medication,
				dm.si_registrated,
				dm.ds_registrated,
				dm.si_without_medication
			from (
				-- With all fields

				SELECT	coalesce(obter_desc_material(a.cd_material),a.ds_medicamento) nm_medication,
					CASE WHEN coalesce(a.cd_material::text, '') = '' THEN  'N'  ELSE 'S' END  si_registrated,
					CASE WHEN coalesce(a.cd_material::text, '') = '' THEN  wheb_mensagem_pck.get_texto(309709)  ELSE wheb_mensagem_pck.get_texto(309708) END  ds_registrated,
					'N' si_without_medication
				from 	paciente_medic_uso a
				where	coalesce(obter_desc_material(a.cd_material),a.ds_medicamento) is not null
				
union all

				-- Without medication

				select	wheb_mensagem_pck.get_texto(369520) nm_medication,
					CASE WHEN coalesce(a.cd_material::text, '') = '' THEN  'N'  ELSE 'S' END  si_registrated,
					CASE WHEN coalesce(a.cd_material::text, '') = '' THEN  wheb_mensagem_pck.get_texto(309709)  ELSE wheb_mensagem_pck.get_texto(309708) END  ds_registrated,
					'N' si_without_medication
				from	paciente_medic_uso a
				
union all

				-- Without medication and registration

				select	wheb_mensagem_pck.get_texto(369520) nm_medication,
					'N' si_registrated,
					wheb_mensagem_pck.get_texto(309709) ds_registrated,
					'S' si_without_medication
				
			) dm
			group by	dm.nm_medication,
					dm.si_registrated,
					dm.ds_registrated,
					dm.si_without_medication
			) x
		on (	(y.nm_medication = x.nm_medication and
		        y.si_registrated = x.si_registrated and
		        y.ds_registrated = x.ds_registrated) or (y.nr_sequencia = 0))
		when not matched then
			insert(y.nr_sequencia, 
				y.dt_atualizacao, 
				y.nm_usuario, 
				y.dt_atualizacao_nrec, 
				y.nm_usuario_nrec, 
				y.nm_medication, 
				y.si_registrated,
				y.ds_registrated)
			values (CASE WHEN x.si_without_medication='S' THEN  0  ELSE nextval('hdm_indic_dm_medication_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.nm_medication,
				x.si_registrated,
				x.ds_registrated);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hdm_indic_pck.populate_dim_medication () FROM PUBLIC;
