SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_partic_data () AS $body$
BEGIN
		merge into hdm_indic_dm_partic_det y	
		using (	SELECT	a.si_status,
				a.ds_status,
				b.si_situation,
				b.ds_situation
			from (	select	vd.vl_dominio si_status,
						vd.ds_valor_dominio ds_status
					from	valor_dominio_v vd
					where	vd.cd_dominio = 6978 and vd.ie_situacao = 'A'
					
union

					SELECT	'0' si_status,
						obter_desc_expressao(704953) ds_status
					) a,
				(	select	vd.vl_dominio si_situation,
						vd.ds_valor_dominio ds_situation
					from	valor_dominio_v vd
					where	vd.cd_dominio = 6979 and vd.ie_situacao = 'A'
					
union

					select	'0' si_situation,
						obter_desc_expressao(697358) ds_situation
					) b
			) x
		on (	y.si_situation = x.si_situation and
			y.si_status = x.si_status)
		when not matched then 
			insert(y.nr_sequencia, 
				y.dt_atualizacao,
				y.nm_usuario,
				y.dt_atualizacao_nrec,
				y.nm_usuario_nrec,
				y.si_situation,
				y.si_status,
				y.ds_situation,
				y.ds_status)
			values (nextval('hdm_indic_dm_partic_det_seq'),
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.si_situation,
				x.si_status,
				x.ds_situation,
				x.ds_status);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
