SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_procedure () AS $body$
BEGIN
		merge into hdm_indic_dm_procedure y
		using(	SELECT	a.nm_type nm_procedure_type,
				'N' si_without_proced 
			from	hdm_indic_procedure_type a 
			where	a.ie_situacao = 'A'
			
union

			SELECT	wheb_mensagem_pck.get_texto(375695) nm_procedure_type,
				'S' si_without_proced 
			
			) x
		on (	(y.nm_procedure_type = x.nm_procedure_type) or (y.nr_sequencia = 0))
		when not matched then
			insert(y.nr_sequencia, 
				y.dt_atualizacao, 
				y.nm_usuario, 
				y.dt_atualizacao_nrec, 
				y.nm_usuario_nrec, 
				y.nm_procedure_type)
			values (CASE WHEN x.si_without_proced='S' THEN  0  ELSE nextval('hdm_indic_dm_procedure_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.nm_procedure_type);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
