SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_professional () AS $body$
BEGIN
		merge into hdm_indic_dm_professional y
		using(	/* With all fields */

			SELECT	obter_nome_pf(a.cd_pessoa_fisica) nm_professional,
					d.nm_funcao nm_responsability,
					c.nm_equipe nm_team,
					'N' si_without_professional
			from	mprev_funcao_colaborador d,
					mprev_equipe c,
					mprev_equipe_profissional b,
					pessoa_fisica a
			where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
			and   	d.NR_SEQUENCIA = b.NR_SEQ_FUNCAO
			and   	c.NR_SEQUENCIA = b.NR_SEQ_EQUIPE
			
union

			/* Without reponsability */


			SELECT	obter_nome_pf(a.cd_pessoa_fisica) nm_professional,
					wheb_mensagem_pck.get_texto(382949) nm_responsability,
					c.nm_equipe nm_team,
					'N' si_without_professional
			from	mprev_equipe c,
					mprev_equipe_profissional b,
					pessoa_fisica a
			where 	c.NR_SEQUENCIA = b.NR_SEQ_EQUIPE 
			and 	a.cd_pessoa_fisica = b.cd_pessoa_fisica
			
union

			/* Without team */


			select	obter_nome_pf(a.cd_pessoa_fisica) nm_professional,
					d.nm_funcao nm_responsability,
					wheb_mensagem_pck.get_texto(382951) nm_team,
					'N' si_without_professional
			from	mprev_funcao_colaborador d,
					mprev_equipe_profissional b,
					pessoa_fisica a
			where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
			and   	d.NR_SEQUENCIA = b.NR_SEQ_FUNCAO   
			
union

			/* Without reponsability and team */


			select	obter_nome_pf(a.cd_pessoa_fisica) nm_professional,
					wheb_mensagem_pck.get_texto(382949) nm_responsability,
					wheb_mensagem_pck.get_texto(382951) nm_team,
					'N' si_without_professional
			from	mprev_equipe_profissional b,
					pessoa_fisica a
			where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
			
union

			/* Without professional, reponsability and team */


			select	wheb_mensagem_pck.get_texto(382947) nm_professional,
					wheb_mensagem_pck.get_texto(382949) nm_responsability,
					wheb_mensagem_pck.get_texto(382951) nm_team,
					'S' si_without_professional
			
			) x
		on (	(y.nm_professional = x.nm_professional and
			y.nm_responsability = x.nm_responsability and
			y.nm_team = x.nm_team) or (y.nr_sequencia = 0))
		when not matched then
			insert(y.nr_sequencia, 
				y.dt_atualizacao, 
				y.nm_usuario, 
				y.dt_atualizacao_nrec, 
				y.nm_usuario_nrec, 
				y.nm_professional, 
				y.nm_responsability, 
				y.nm_team)
			values (CASE WHEN x.si_without_professional='S' THEN  0  ELSE nextval('hdm_indic_dm_professional_seq') END ,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.nm_professional,
				x.nm_responsability,
				x.nm_team);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hdm_indic_pck.populate_dim_professional () FROM PUBLIC;
