SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_dim_vaccine () AS $body$
BEGIN
		merge into hdm_indic_dm_vaccine y
		using(	SELECT	a.ds_vacina nm_vaccine,
				a.ds_vacina || ' (' ||  b.ds_valor_dominio || ')' nm_vaccine_dose
			from	vacina a,
				valor_dominio_v b
			where	b.ie_situacao = 'A'
			and	b.cd_dominio = 1018
			) x
		on (	y.nm_vaccine = x.nm_vaccine and
			y.nm_vaccine_dose = x.nm_vaccine_dose)
		when not matched then
			insert(y.nr_sequencia, 
				y.dt_atualizacao, 
				y.nm_usuario, 
				y.dt_atualizacao_nrec, 
				y.nm_usuario_nrec, 
				y.nm_vaccine, 
				y.nm_vaccine_dose)
			values (nextval('hdm_indic_dm_vaccine_seq'),
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				x.nm_vaccine,
				x.nm_vaccine_dose);
		commit;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hdm_indic_pck.populate_dim_vaccine () FROM PUBLIC;
