SET client_encoding TO 'UTF8';

	
	/*			*
	*	DISEASE FACTS	*
	*			*/
	
	
CREATE TYPE disease_data AS (nr_seq_clinic_ant	bigint,
				dt_start_adjusted	timestamp,
				dt_end_adjusted		timestamp,
				cd_pessoa_fisica	pessoa_fisica.cd_pessoa_fisica%type,
				ie_sexo			pessoa_fisica.ie_sexo%type,
				nr_seq_participante	mprev_participante.nr_sequencia%type,
				dt_nascimento		pessoa_fisica.dt_nascimento%type,
				nr_seq_patient_group	bigint);


CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_ft_disease () AS $body$
DECLARE

	i			bigint	:= 1;
	j			bigint	:= 1;
	k			bigint	:= 1;
	nr_seq_patient_group_w	bigint	:= null;
	
	vector_person_w			vector_person;
	type vector_disease_data is table of disease_data index by integer;
	
	vector_disease_data_w		vector_disease_data;
	
	c_persons CURSOR(dt_start_pc timestamp, dt_end_pc timestamp) FOR
		SELECT	/*+ USE_CONCAT */
			distinct
			/*+ USE_CONCAT */

			pf.cd_pessoa_fisica,
			pf.ie_sexo,
			pt.nr_sequencia nr_seq_participante,
			pf.dt_nascimento
		FROM paciente_antec_clinico ac, pessoa_fisica pf
LEFT OUTER JOIN mprev_participante pt ON (pf.cd_pessoa_fisica = pt.cd_pessoa_fisica)
WHERE ac.dt_liberacao between dt_start_pc and dt_end_pc and ac.cd_pessoa_fisica = pf.cd_pessoa_fisica;		
		
	c_disease_data CURSOR(dt_start_pc timestamp, dt_end_pc timestamp, cd_pessoa_fisica_pc text) FOR
		SELECT	distinct
			coalesce(ac.ie_status,'0')  si_state,
			coalesce(coalesce(obter_desc_cid_doenca(ac.cd_doenca),coalesce(obter_desc_pe_doenca(ac.nr_seq_doenca),ac.ds_doenca)),wheb_mensagem_pck.get_texto(365921)) nm_disease,
			coalesce(ac.dt_inicio, ac.dt_liberacao) dt_start_adjusted,
			coalesce(ac.dt_inativacao, ac.dt_fim) dt_end_adjusted
		from	paciente_antec_clinico ac
		where	ac.dt_liberacao between dt_start_pc and dt_end_pc
		and	ac.cd_pessoa_fisica = cd_pessoa_fisica_pc;
	
BEGIN		
		-- Generate person vector 

		i	:= 1;
		for r_c_persons in c_persons(current_setting('hdm_indic_pck.dt_start_w')::timestamp,current_setting('hdm_indic_pck.dt_end_w')::timestamp) loop
			nr_seq_patient_group_w	:= hdm_indic_pck.get_patient_group(	r_c_persons.cd_pessoa_fisica,
									r_c_persons.nr_seq_participante,
									r_c_persons.dt_nascimento,
									r_c_persons.ie_sexo);
									
			vector_person_w[i].cd_pessoa_fisica 		:= r_c_persons.cd_pessoa_fisica;
			vector_person_w[i].nr_seq_participante 		:= r_c_persons.nr_seq_participante;
			vector_person_w[i].dt_nascimento 		:= r_c_persons.dt_nascimento;
			vector_person_w[i].ie_sexo 			:= r_c_persons.ie_sexo;
			vector_person_w[i].nr_seq_patient_group 	:= nr_seq_patient_group_w;
			i	:= i + 1;
		end loop;

		-- Open person vector 

		j	:= 1;
		i := vector_person_w.count;
		for k in 1.. i loop
			for r_c_disease_data in c_disease_data(	current_setting('hdm_indic_pck.dt_start_w')::timestamp,
								current_setting('hdm_indic_pck.dt_end_w')::timestamp,
								vector_person_w[k].cd_pessoa_fisica) loop							
				vector_disease_data_w[j].cd_pessoa_fisica 	:= vector_person_w[k].cd_pessoa_fisica;
				vector_disease_data_w[j].nr_seq_participante 	:= vector_person_w[k].nr_seq_participante;
				vector_disease_data_w[j].dt_nascimento 		:= vector_person_w[k].dt_nascimento;
				vector_disease_data_w[j].ie_sexo 		:= vector_person_w[k].ie_sexo;
				vector_disease_data_w[j].nr_seq_patient_group 	:= vector_person_w[k].nr_seq_patient_group;	
		
				vector_disease_data_w[j].nr_seq_clinic_ant	:= hdm_indic_pck.get_clinic_ant(r_c_disease_data.si_state, r_c_disease_data.nm_disease);
				vector_disease_data_w[j].dt_start_adjusted	:= r_c_disease_data.dt_start_adjusted;
				vector_disease_data_w[j].dt_end_adjusted	:= r_c_disease_data.dt_end_adjusted;
				j	:= j + 1;
			end loop;
		end loop;

		-- Open disease data vector 

		PERFORM set_config('hdm_indic_pck.qt_record_w', 0, false);
		i := vector_disease_data_w.count;
		for k in 1.. i loop
			insert into hdm_indic_ft_clinic_ant(	nr_sequencia,
					dt_atualizacao, 
					nm_usuario, 
					dt_atualizacao_nrec, 
					nm_usuario_nrec, 
					nr_seq_clinic_ant, 
					nr_seq_day_start, 
					nr_seq_month_start, 
					nr_seq_day_end, 
					nr_seq_month_end, 
					nr_seq_patient_group, 
					nr_dif_person)
			SELECT	nextval('hdm_indic_ft_clinic_ant_seq'),
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				vector_disease_data_w[k].nr_seq_clinic_ant,
				(	SELECT	max(x.nr_sequencia)
					from	hdm_indic_dm_day x
					where	x.dt_complete_date = pkg_date_utils.start_of(vector_disease_data_w[k].dt_start_adjusted,'DD', 0)) nr_seq_day_start,
				(	select	max(x.nr_sequencia)
					from	hdm_indic_dm_month x              
					where	x.dt_complete_date = pkg_date_utils.start_of(vector_disease_data_w[k].dt_start_adjusted,'MONTH', 0)) nr_seq_month_start,
				(	select	max(x.nr_sequencia)                    
					from	hdm_indic_dm_day x                
					where	x.dt_complete_date = pkg_date_utils.start_of(vector_disease_data_w[k].dt_end_adjusted,'DD', 0)) nr_seq_day_end,
				(	select	max(x.nr_sequencia)                    
					from	hdm_indic_dm_month x              
					where	x.dt_complete_date = pkg_date_utils.start_of(vector_disease_data_w[k].dt_end_adjusted,'MONTH', 0)) nr_seq_month_end,
				vector_disease_data_w[k].nr_seq_patient_group,
				vector_disease_data_w[k].cd_pessoa_fisica
			;
			
			if (current_setting('hdm_indic_pck.qt_record_w')::integer >= current_setting('hdm_indic_pck.qt_rec_commit_w')::integer) then
				commit;
				PERFORM set_config('hdm_indic_pck.qt_record_w', 0, false);
			end if;
			PERFORM set_config('hdm_indic_pck.qt_record_w', current_setting('hdm_indic_pck.qt_record_w')::integer + 1, false);
			
		end loop;
		
		commit;
		
		CALL hdm_indic_pck.link_campaign_with_di_fact();
		CALL hdm_indic_pck.link_activ_group_with_di_fact();
		CALL hdm_indic_pck.link_program_with_di_fact();
		CALL hdm_indic_pck.link_risk_disease_with_di_fact();		
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hdm_indic_pck.populate_ft_disease () FROM PUBLIC;
