SET client_encoding TO 'UTF8';

	
	/*				*
	*	TEAM EPISODES FACTS		*
	*				*/

	
CREATE TYPE episode_data AS (	nr_seq_episode_det	bigint,
					nr_min_duration		hdm_indic_ft_episode.nr_min_duration%type,
					dt_reference		timestamp,
					cd_pessoa_fisica	pessoa_fisica.cd_pessoa_fisica%type,
					ie_sexo			pessoa_fisica.ie_sexo%type,
					nr_seq_participante	mprev_participante.nr_sequencia%type,
					dt_nascimento		pessoa_fisica.dt_nascimento%type,
					nr_seq_patient_group	bigint);


CREATE OR REPLACE PROCEDURE hdm_indic_pck.populate_ft_team_episodes () AS $body$
DECLARE

	i			bigint	:= 1;
	j			bigint	:= 1;
	k			bigint	:= 1;
	nr_seq_patient_group_w	bigint	:= null;
	
	vector_person_w			vector_person;
	type vector_episode_data is table of episode_data index by integer;
	
	vector_episode_data_w		vector_episode_data;
	
	c_persons CURSOR(dt_start_pc timestamp, dt_end_pc timestamp) FOR
		SELECT	/*+ USE_CONCAT */
			distinct
			/*+ USE_CONCAT */

			pf.cd_pessoa_fisica,
			pf.ie_sexo,
			pt.nr_sequencia nr_seq_participante,
			pf.dt_nascimento
		from	mprev_atend_paciente pac,
			mprev_participante pt,
			pessoa_fisica pf,
			mprev_atendimento ep
		where	ep.dt_inicio between dt_start_pc and dt_end_pc 
		and	ep.nr_sequencia = pac.nr_seq_atendimento
		and	pac.nr_seq_participante = pt.nr_sequencia 
		and	pf.cd_pessoa_fisica = pt.cd_pessoa_fisica;
		
	c_episode_data CURSOR(dt_start_pc timestamp, dt_end_pc timestamp, cd_pessoa_fisica_pc text) FOR
		SELECT	distinct
			ep.dt_inicio dt_reference,
			((coalesce(ep.dt_termino,ep.dt_inicio) - ep.dt_inicio) * 1440) nr_min_duration,
			coalesce(ep.ie_forma_atendimento,'0') si_form,
			coalesce(ep.ie_individual_coletivo,'0') si_type,
			(	SELECT	max(mprev_obter_dados_agenda(z.cd_agenda, 'NM'))
				from	mprev_local_atend_agenda z
				where	exists (select	1
						from	mprev_agendamento a,
							agenda b
						where	b.cd_agenda = z.cd_agenda
						and	a.nr_seq_mprev_atend = ep.nr_sequencia
						and	a.cd_agenda = b.cd_agenda)) nm_place
		from	mprev_atend_paciente pac,
			mprev_participante pt,
			mprev_atendimento ep
		where	ep.dt_inicio between dt_start_pc and dt_end_pc 
		and	ep.nr_sequencia = pac.nr_seq_atendimento
		and	pac.nr_seq_participante = pt.nr_sequencia 
		and	pt.cd_pessoa_fisica = cd_pessoa_fisica_pc;
	
BEGIN			
		/* Generate person vector */


		i	:= 1;
		for r_c_persons in c_persons(current_setting('hdm_indic_pck.dt_start_w')::timestamp,current_setting('hdm_indic_pck.dt_end_w')::timestamp) loop
			nr_seq_patient_group_w	:= hdm_indic_pck.get_patient_group(	r_c_persons.cd_pessoa_fisica,
									r_c_persons.nr_seq_participante,
									r_c_persons.dt_nascimento,
									r_c_persons.ie_sexo);
									
			vector_person_w[i].cd_pessoa_fisica 		:= r_c_persons.cd_pessoa_fisica;
			vector_person_w[i].nr_seq_participante 		:= r_c_persons.nr_seq_participante;
			vector_person_w[i].dt_nascimento 		:= r_c_persons.dt_nascimento;
			vector_person_w[i].ie_sexo 			:= r_c_persons.ie_sexo;
			vector_person_w[i].nr_seq_patient_group 	:= nr_seq_patient_group_w;
			i	:= i + 1;
		end loop;

		/* Open person vector */


		j	:= 1;
		i := vector_person_w.count;
		for k in 1.. i loop
			for	r_c_episode_data in c_episode_data(	current_setting('hdm_indic_pck.dt_start_w')::timestamp,
									current_setting('hdm_indic_pck.dt_end_w')::timestamp,
									vector_person_w[k].cd_pessoa_fisica) loop
				
				vector_episode_data_w[j].cd_pessoa_fisica 	:= vector_person_w[k].cd_pessoa_fisica;
				vector_episode_data_w[j].nr_seq_participante 	:= vector_person_w[k].nr_seq_participante;
				vector_episode_data_w[j].dt_nascimento 		:= vector_person_w[k].dt_nascimento;
				vector_episode_data_w[j].ie_sexo 		:= vector_person_w[k].ie_sexo;
				vector_episode_data_w[j].nr_seq_patient_group 	:= vector_person_w[k].nr_seq_patient_group;	

				vector_episode_data_w[j].dt_reference		:= r_c_episode_data.dt_reference;
				vector_episode_data_w[j].nr_seq_episode_det	:= hdm_indic_pck.get_episode_det(r_c_episode_data.si_form, r_c_episode_data.si_type, r_c_episode_data.nm_place);
				vector_episode_data_w[j].nr_min_duration	:= r_c_episode_data.nr_min_duration;
						
				j	:= j + 1;
			end loop;
		end loop;

		-- Open episodes data vector 

		PERFORM set_config('hdm_indic_pck.qt_record_w', 0, false);
		i := vector_episode_data_w.count;
		
		for k in 1.. i loop
			insert into hdm_indic_ft_episode(	nr_sequencia,
					dt_atualizacao, 
					nm_usuario, 
					dt_atualizacao_nrec, 
					nm_usuario_nrec, 
					nr_seq_episode_det, 
					nr_min_duration, 
					nr_seq_day, 
					nr_seq_month, 
					nr_seq_patient_group, 
					nr_dif_person,
					ds_unique)
			SELECT	nextval('hdm_indic_ft_episode_seq'),
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				clock_timestamp(),
				current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type,
				vector_episode_data_w[k].nr_seq_episode_det,
				vector_episode_data_w[k].nr_min_duration,
				(	
					SELECT	max(x.nr_sequencia)
					from	hdm_indic_dm_day x
					where	x.dt_complete_date = pkg_date_utils.start_of(vector_episode_data_w[k].dt_reference, 'DD', 0)
					and 	x.nm_usuario = current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type
				) nr_seq_day,
				(	
					select	max(x.nr_sequencia)
					from	hdm_indic_dm_month x
					where	x.dt_complete_date = pkg_date_utils.start_of(vector_episode_data_w[k].dt_reference, 'MONTH', 0)
					and 	x.nm_usuario = current_setting('hdm_indic_pck.nm_usuario_w')::usuario.nm_usuario%type
				) nr_seq_month,
				vector_episode_data_w[k].nr_seq_patient_group,
				vector_episode_data_w[k].cd_pessoa_fisica,
				current_setting('hdm_indic_pck.ds_unique_w')::varchar(255)
			;
				
			if (current_setting('hdm_indic_pck.qt_record_w')::integer >= current_setting('hdm_indic_pck.qt_rec_commit_w')::integer) then
				commit;
				PERFORM set_config('hdm_indic_pck.qt_record_w', 0, false);
			end if;
			PERFORM set_config('hdm_indic_pck.qt_record_w', current_setting('hdm_indic_pck.qt_record_w')::integer + 1, false);
		end loop;
		
		commit;
		
		CALL hdm_indic_pck.link_campaign_with_ep_fact();
		CALL hdm_indic_pck.link_activ_group_with_ep_fact();
		CALL hdm_indic_pck.link_program_with_ep_fact();
		
		CALL hdm_indic_pck.link_professional_whit_ep_fact();
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hdm_indic_pck.populate_ft_team_episodes () FROM PUBLIC;
