CREATE OR REPLACE FUNCTION holding_pck.get_estab_intercompany (cd_cgc_emitente_p text) RETURNS bigint AS $body$
DECLARE

cd_estab_cgc_w	estabelecimento.cd_estabelecimento%type;

BEGIN
if (coalesce(cd_cgc_emitente_p, '0') <> '0') then
	begin
	select  coalesce(max(cd_estabelecimento), 0)
	into STRICT  cd_estab_cgc_w
	from  estabelecimento e
	where  e.cd_cgc = cd_cgc_emitente_p;
	exception
		when no_data_found then
		cd_estab_cgc_w := 0;
	end;
end if;
return cd_estab_cgc_w;
END;

