CREATE OR REPLACE FUNCTION holding_pck.get_grupo_emp_estrut_vigente (cd_empresa_p bigint) RETURNS bigint AS $body$
DECLARE

  nr_sequencia_w  grupo_empresa.nr_sequencia%type;

BEGIN
  begin
    select  a.nr_seq_grupo
    into STRICT  nr_sequencia_w
    from  grupo_emp_estrutura a,
      grupo_empresa b
    where  a.cd_empresa    =  cd_empresa_p
    and  a.dt_inicio_vigencia  <=  trunc(clock_timestamp())
    and (a.dt_fim_vigencia  >=  trunc(clock_timestamp()) or
      coalesce(a.dt_fim_vigencia::text, '') = '')
    and  a.nr_seq_grupo    =  b.nr_sequencia
    and  b.dt_inicio_vigencia  <=  trunc(clock_timestamp())
    and (b.dt_fim_vigencia  >=  trunc(clock_timestamp()) or 
      coalesce(b.dt_fim_vigencia::text, '') = '')
      and a.ie_tipo_estrutura = 1;   -- Pegar apenas a Controladora
  exception when others then
                Begin
                        select  coalesce(max(a.nr_seq_grupo),0)
                        into STRICT  nr_sequencia_w
                        from  grupo_emp_estrutura a,
                                grupo_empresa b
                        where  a.cd_empresa    =  cd_empresa_p
                        and  a.dt_inicio_vigencia  <=  trunc(clock_timestamp())
                        and (a.dt_fim_vigencia  >=  trunc(clock_timestamp()) or
                                coalesce(a.dt_fim_vigencia::text, '') = '')
                        and  a.nr_seq_grupo    =  b.nr_sequencia
                        and  b.dt_inicio_vigencia  <=  trunc(clock_timestamp())
                        and (b.dt_fim_vigencia  >=  trunc(clock_timestamp()) or 
                                coalesce(b.dt_fim_vigencia::text, '') = '');
                exception
                        when others then
                                        nr_sequencia_w:= 0;
                end;
  end;

  return nr_sequencia_w;
END;

$body$
