CREATE OR REPLACE FUNCTION holding_pck.get_grupo_emp_usuario_vigente (cd_empresa_p bigint) RETURNS bigint AS $body$
DECLARE

  nr_sequencia_w  grupo_empresa.nr_sequencia%type;

BEGIN
  begin
    select  coalesce(max(nr_seq_grupo),0)
    into STRICT  nr_sequencia_w
    from  grupo_emp_estrutura
    where  cd_empresa    =  cd_empresa_p
    and  dt_inicio_vigencia  <=  trunc(clock_timestamp())
    and (dt_fim_vigencia  >=  trunc(clock_timestamp()) or
      dt_fim_vigencia    is  null);
  exception
    when no_data_found then
      nr_sequencia_w:= 0;
  end;

  return nr_sequencia_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
