CREATE OR REPLACE FUNCTION holding_pck.get_nm_grupo_empresa ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type default null, cd_empresa_p empresa.cd_empresa%type default null, nr_sequencia_p grupo_empresa.nr_sequencia%type default null) RETURNS varchar AS $body$
DECLARE


  nm_grupo_empresa_w varchar(255);


BEGIN

  select  max(nm_grupo_empresa)
  into STRICT  nm_grupo_empresa_w
  from  grupo_empresa
  where  nr_sequencia =   (
    SELECT  max(a.nr_sequencia)
    from  grupo_empresa a, 
      grupo_emp_estrutura b, 
      estabelecimento c
    where  a.nr_sequencia  =  b.nr_seq_grupo
    and  b.cd_empresa  =  c.cd_empresa
    and (c.cd_estabelecimento  = cd_estabelecimento_p or c.cd_empresa = cd_empresa_p or a.nr_sequencia = nr_sequencia_p));

  return(nm_grupo_empresa_w);

END;

