CREATE OR REPLACE FUNCTION holding_pck.get_se_intercompany_cnpj ( cd_estabelecimento_p bigint, cd_cgc_emitente_p text) RETURNS varchar AS $body$
DECLARE

cd_estab_info_w    estabelecimento.cd_estabelecimento%type;
ie_intercompany_w    varchar(1)    := 'N';


BEGIN
if (coalesce(cd_cgc_emitente_p, '0') <> '0' and coalesce(cd_estabelecimento_p, 0) <> 0) then
  begin
  select  coalesce(max(cd_estabelecimento), 0)
  into STRICT  cd_estab_info_w
  from  estabelecimento e
  where  e.cd_cgc = cd_cgc_emitente_p;
  exception
    when no_data_found then
    cd_estab_info_w := 0;
  end;
  if (cd_estab_info_w <> 0) then
    ie_intercompany_w   := holding_pck.get_se_intercompany(cd_estabelecimento_p, cd_estab_info_w);
  end if;
end if;
return ie_intercompany_w;
END;

