CREATE OR REPLACE FUNCTION holding_pck.obter_info_conta_fin_control (cd_conta_financ_p bigint, ds_info_p text) RETURNS varchar AS $body$
DECLARE

    ds_info_financ_controladora_w conta_financeira.ds_conta_financ%type;

BEGIN
    select case ds_info_p
           when 'DESCRICAO' then a.ds_conta_estrut
           when 'CONTA_APRESENTACAO' then a.cd_conta_apres
           when 'COR' then a.ds_cor 
           when 'COR_FUNDO' then a.ds_cor_fundo
           when 'CONTA_FINANC' then to_char(a.cd_conta_financ)
           end           
      into STRICT ds_info_financ_controladora_w
      from conta_financeira_v a
     where a.cd_conta_financ = cd_conta_financ_p;

    return ds_info_financ_controladora_w;

END;

$body$
LANGUAGE PLPGSQL
