CREATE OR REPLACE PROCEDURE hpms_proposal_request_pck.update_payment_payer (nr_seq_prop_online_p pls_proposta_online.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

cd_condicao_pagamento_w		pls_parametros.cd_condicao_pagamento%type;

C01 CURSOR FOR
	SELECT	b.nr_sequencia nr_seq_pagador,
		b.cd_pessoa_fisica,
		d.ds_email,
		c.nr_dia_vencimento,
		c.cd_estabelecimento,
		c.dt_confirmacao,
		coalesce(c.nr_seq_forma_cobranca,'1') nr_seq_forma_cobranca,
		c.cd_banco,
		c.cd_conta,
		c.ie_digito_conta,
		c.cd_agencia_bancaria,
		c.ie_digito_agencia,
		coalesce(c.ie_boleto_email,'N') ie_boleto_email,
		(	SELECT	max(x.nr_sequencia)
			from	pls_contrato_pagador_fin x
			where	x.nr_seq_pagador = b.nr_sequencia) nr_seq_pagador_fin
	from	pls_contrato a,
		pls_contrato_pagador b,
		pls_proposta_online c,
		pls_proposta_benef_online d
	where	a.nr_seq_prop_online = nr_seq_prop_online_p
	and	b.nr_seq_contrato = a.nr_sequencia
	and	c.nr_sequencia = d.nr_seq_prop_online
	and	d.cd_pessoa_fisica = b.cd_pessoa_fisica;

BEGIN
	
	select	max(cd_condicao_pagamento)
	into STRICT	cd_condicao_pagamento_w
	from	pls_parametros
	where	cd_estabelecimento = cd_estabelecimento_p;

	for C01_w in C01 loop
		begin
			if (C01_w.ie_boleto_email = 'S') then
				update	pls_contrato_pagador
				set	ie_envia_cobranca	= 'E',
					nm_usuario		= nm_usuario_p,
					dt_atualizacao		= clock_timestamp()
				where	nr_sequencia		= C01_w.nr_seq_pagador;
			end if;

			update	pls_contrato_pagador_fin
			set	dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p,
				dt_dia_vencimento = C01_w.nr_dia_vencimento,
				nr_seq_forma_cobranca = coalesce(C01_w.nr_seq_forma_cobranca,'1'),
				ie_geracao_nota_titulo = 'T',
				cd_condicao_pagamento = cd_condicao_pagamento_w,
				cd_banco = C01_w.cd_banco,
				cd_conta = C01_w.cd_conta,
				ie_digito_conta = C01_w.ie_digito_conta,
				cd_agencia_bancaria = C01_w.cd_agencia_bancaria,
				ie_digito_agencia = C01_w.ie_digito_agencia
			where	nr_sequencia = c01_w.nr_seq_pagador_fin;
		end;
	end loop;
	
	commit;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
