CREATE OR REPLACE PROCEDURE hpms_proposal_request_pck.wsuite_generate_analysis ( nr_seq_proposta_p pls_proposta_online.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Create adhesion analysis
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


nr_seq_analise_adesao_w		pls_analise_adesao.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	a.dt_confirmacao,
		a.cd_estabelecimento,
		b.cd_pessoa_fisica,
		b.nr_sequencia nr_seq_prop_benef
	from	pls_proposta_online a,
		pls_proposta_benef_online b
	where	b.nr_seq_prop_online	= a.nr_sequencia
	and	a.nr_sequencia		= nr_seq_proposta_p
	and b.ie_tipo_benef <> 'R';

BEGIN

for C01_w in C01 loop
	begin
	insert	into pls_analise_adesao(nr_sequencia, cd_estabelecimento, nr_seq_prop_online,
		nr_seq_prop_benef_online, cd_pessoa_fisica, dt_analise,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec, ie_status, nm_usuario_analise,
		dt_solicitacao_inclusao)
	values (	nextval('pls_analise_adesao_seq'), C01_w.cd_estabelecimento, nr_seq_proposta_p,
		C01_w.nr_seq_prop_benef, C01_w.cd_pessoa_fisica, clock_timestamp(),
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		nm_usuario_p, 'A', nm_usuario_p,
		C01_w.dt_confirmacao) returning nr_sequencia into nr_seq_analise_adesao_w;
		
	CALL hpms_proposal_request_pck.wsuite_generate_incidents(nr_seq_analise_adesao_w, nm_usuario_p);
	end;
end loop;

update	pls_proposta_online
set	ie_estagio	= 'AA',
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_proposta_p;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
