SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE hpms_proposal_request_pck.wsuite_set_natural_person ( nr_seq_prop_benef_p pls_proposta_benef_online.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Add the cd_pessoa_fisica to health statement and wsuite user
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ x ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 

cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
ie_tipo_benef_w		pls_proposta_benef_online.ie_tipo_benef%type;
id_subject_w		wsuite_usuario.id_subject%type;


BEGIN

select	a.ie_tipo_benef,
	a.cd_pessoa_fisica,
	b.id_subject
into STRICT	ie_tipo_benef_w,
	cd_pessoa_fisica_w,
	id_subject_w
from	pls_proposta_benef_online a,
	pls_proposta_online b
where	a.nr_seq_prop_online	= b.nr_sequencia
and	a.nr_sequencia		= nr_seq_prop_benef_p;

if (ie_tipo_benef_w <> 'R') and (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	update	pls_declaracao_segurado
	set	cd_pessoa_fisica		= cd_pessoa_fisica_w,
		nm_usuario			= nm_usuario_p,
		dt_atualizacao			= clock_timestamp()
	where	nr_seq_prop_benef_online	= nr_seq_prop_benef_p;
	
	if (ie_tipo_benef_w = 'T') then
		update	wsuite_usuario
		set	cd_pessoa_fisica	= cd_pessoa_fisica_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	id_subject		= id_subject_w;
	end if;
	
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
