SET client_encoding TO 'UTF8';

-- Atualiza data e hora da integração
CREATE OR REPLACE PROCEDURE igtln_integracao_pck.apae_ws_atualiza_lote (nr_lote_p bigint) AS $body$
BEGIN
		if (nr_lote_p > 0) then
			update	lote_ent_secretaria
			set		dt_integracao = clock_timestamp(),
					ie_integracao = 'S',
					qt_fichas_lote = (SELECT count(1) from lote_ent_sec_ficha where nr_seq_lote_sec = nr_lote_p)
			where	nr_sequencia = nr_lote_p;
		end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
