SET client_encoding TO 'UTF8';


	-- Grava lote
CREATE OR REPLACE PROCEDURE igtln_integracao_pck.apae_ws_grava_lote ( nr_lote_p bigint, nr_seq_instituicao_p bigint, nr_seq_posto_p bigint, ie_tipo_ficha_p text, nr_seq_pacote_p bigint, nm_usuario_p text, nr_sequencia_out_p INOUT bigint) AS $body$
DECLARE

	nr_sequencia_w	bigint;
	nr_seq_pacote_w bigint;
	
BEGIN
		-- Se tipo de ficha for igual a N, cadastros de Pacotes é Obrigatório
		if (ie_tipo_ficha_p = 'N') then
			select	max(nr_sequencia)
			into STRICT	nr_sequencia_w
			from	lote_ent_secretaria
			where	nr_lote = nr_lote_p
			and		nr_seq_instituicao = nr_seq_instituicao_p
			and		nr_seq_posto = nr_seq_posto_p
			and		ie_tipo_ficha = ie_tipo_ficha_p
			and		nr_seq_pacote = nr_seq_pacote_p;

			nr_seq_pacote_w := nr_seq_pacote_p;
		else
			select	max(nr_sequencia)
			into STRICT	nr_sequencia_w
			from	lote_ent_secretaria
			where	nr_lote = nr_lote_p
			and		nr_seq_instituicao = nr_seq_instituicao_p
			and		nr_seq_posto = nr_seq_posto_p
			and		ie_tipo_ficha = ie_tipo_ficha_p;

			nr_seq_pacote_w := null;
		end if;

		-- se não achou, insere registro
		if (coalesce(nr_sequencia_w::text, '') = '') then
			select	nextval('lote_ent_secretaria_seq')
			into STRICT	nr_sequencia_w
			;

			insert into lote_ent_secretaria(
				nr_sequencia,
				nr_seq_instituicao,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_tipo_ficha,
				dt_recebimento,
				nr_seq_pacote,
				nr_lote,
				nr_seq_posto
			) values (
				nr_sequencia_w,
				nr_seq_instituicao_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ie_tipo_ficha_p,
				clock_timestamp(),
				nr_seq_pacote_w,
				nr_lote_p,
				nr_seq_posto_p
				);
		end if;

	nr_sequencia_out_p := nr_sequencia_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
