	-- Obtem código da pessoa fisica
CREATE OR REPLACE PROCEDURE igtln_integracao_pck.apae_ws_obter_cd_pessoa ( cd_prontuario_p text, nm_paciente_p text, nm_usuario_p text, cd_pessoa_fisica_out_p INOUT text) AS $body$
DECLARE

	cd_pessoa_fisica_w	varchar(10);
	
BEGIN
		-- se encontrar pelo código do prontuario, retorna o cd_pessoa_fisica
		-- senão, insere na tabela pessoas_fisica where nome_p e retorna o novo cd_pessoa_fisica
		if (cd_prontuario_p IS NOT NULL AND cd_prontuario_p::text <> '') then
			select	max(cd_pessoa_fisica)
			into STRICT	cd_pessoa_fisica_w
			from	pessoa_fisica
			where	cd_pessoa_fisica = cd_prontuario_p;
		end if;

		if (coalesce(cd_pessoa_fisica_w::text, '') = '') then
			select	nextval('pessoa_fisica_seq')
			into STRICT	cd_pessoa_fisica_w
			;

			insert into pessoa_fisica(
				cd_pessoa_fisica,
				ie_tipo_pessoa,
				nm_pessoa_fisica,
				dt_atualizacao,
				nm_usuario
			) values (
				cd_pessoa_fisica_w,
				2,
				nm_paciente_p,
				clock_timestamp(),
				nm_usuario_p
				);
		end if;

	cd_pessoa_fisica_out_p := cd_pessoa_fisica_w;

	END;

$body$
