CREATE OR REPLACE PROCEDURE import_users_to_psa_tws_pack.import_contracts_to_group () AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_grupo_contrato_web a,
		pls_grupo_contrato b
	where	a.nr_seq_grupo_contrato	= b.nr_sequencia
	and	b.ie_situacao		= 'A'
	and	not exists (	SELECT 	1
				from 	pls_contrato_web b
				where	a.nr_sequencia	= b.nr_seq_usuario_grupo);
	
C02 CURSOR(nr_seq_grupo_pc bigint) FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_contrato,
		a.nr_seq_intercambio
	from	pls_contrato_grupo a
	where	a.nr_seq_grupo	= nr_seq_grupo_pc;
	
BEGIN

for C01_w in C01 loop
	begin
	for C02_w in C02(C01_w.nr_sequencia) loop
		begin
		insert into pls_contrato_web(	nr_sequencia, ie_situacao, nr_seq_usuario_grupo,
							dt_atualizacao, nm_usuario, dt_atualizacao_nrec,    
							nm_usuario_nrec, nr_seq_contrato, nr_seq_intercambio,     
							nr_seq_subestipulante)
					values (	nextval('pls_contrato_web_seq'), 'A', C01_w.nr_sequencia,	
							clock_timestamp(), 'stipulator', clock_timestamp(),
							'stipulator', C02_w.nr_seq_contrato, C02_w.nr_seq_intercambio,
							null);
		end;
	end loop;
	end;
end loop;

END;

$body$
