CREATE OR REPLACE PROCEDURE importer_kairos_tools.clear_temp_table ( nm_file_a text, nm_file_b text, qt_lines_p bigint ) AS $body$
DECLARE


nr_sequencia_w  		W_KAIROS.NR_SEQUENCIA%type;


BEGIN
	nr_sequencia_w := importer_kairos_tools.get_first_nr_sequencia(nm_file_a, nm_file_b);
	DELETE 	FROM W_KAIROS a
	WHERE 	a.NM_USUARIO = current_setting('importer_kairos_tools.nm_usuario_s')::W_KAIROS.NM_USUARIO%type
	AND 	a.NR_SEQUENCIA BETWEEN nr_sequencia_w AND (nr_sequencia_w + qt_lines_p)
