SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION importer_kairos_tools.get_file_lines ( nm_file_a text, nm_file_b text, qt_lines_p bigint ) RETURNS STRING_LIST AS $body$
DECLARE


nr_sequencia_w			W_KAIROS.NR_SEQUENCIA%type;
ds_campo_w			string_list;


BEGIN
	nr_sequencia_w := importer_kairos_tools.get_first_nr_sequencia(nm_file_a, nm_file_b);

	IF (nr_sequencia_w > 0) THEN
		BEGIN
			SELECT 	a.DS_CAMPO
			BULK COLLECT INTO STRICT ds_campo_w
			FROM 	W_KAIROS a
			WHERE 	a.NM_ARQUIVO IN (nm_file_a, nm_file_b)
			AND 	a.NM_USUARIO = current_setting('importer_kairos_tools.nm_usuario_s')::W_KAIROS.NM_USUARIO%type
			AND     a.NR_SEQUENCIA BETWEEN nr_sequencia_w AND (nr_sequencia_w + qt_lines_p);
		EXCEPTION WHEN no_data_found THEN NULL;
                END;
	END IF;

	RETURN ds_campo_w;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
