CREATE OR REPLACE FUNCTION importer_kairos_tools.get_first_nr_sequencia ( nm_file_a text, nm_file_b text ) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w 			W_KAIROS.NR_SEQUENCIA%type;


BEGIN
	SELECT 	coalesce(MIN(a.NR_SEQUENCIA), 0)
	INTO STRICT 	nr_sequencia_w
	FROM 	W_KAIROS a
	WHERE 	a.NM_ARQUIVO IN (nm_file_a, nm_file_b)
