CREATE OR REPLACE PROCEDURE importer_kairos_tools.save_administration_via ( cd_via_adm_p VIAADM_MATGLOBAL.CD_VIA_ADM%type, ds_via_adm_p VIAADM_MATGLOBAL.DS_VIA_ADM%type, ie_situation_p VIAADM_MATGLOBAL.IE_SITUACAO%type DEFAULT 'A') AS $body$
DECLARE


cd_via_adm_w	VIAADM_MATGLOBAL.CD_VIA_ADM%type;
cd_via_adm_new_w	VIAADM_MATGLOBAL.CD_VIA_ADM%type;	


BEGIN
	SELECT 	MAX(a.CD_VIA_ADM)
	INTO STRICT 	cd_via_adm_w
	FROM 	VIAADM_MATGLOBAL a
	WHERE 	trim(both LOWER(a.DS_VIA_ADM)) = trim(both LOWER(ds_via_adm_p));

	IF (coalesce(cd_via_adm_w::text, '') = '') THEN
		CALL importer_kairos_tools.check_conversion_rule(
			nm_table_p 		=> 'VIAADM_MATGLOBAL',
			nm_attribute_p 		=> 'IE_SITUACAO',
			cd_external_p 		=> 'B',
			cd_internal_p 		=> 'I'
		);

		SELECT MAX(a.CD_VIA_ADM + 1)
		KEEP(DENSE_RANK LAST ORDER BY (REGEXP_REPLACE(a.CD_VIA_ADM, '[^[:digit:]]', ''))::numeric ) LAST_SEQUENCE
		INTO STRICT 	cd_via_adm_new_w
		FROM 	VIAADM_MATGLOBAL a;

		INSERT INTO VIAADM_MATGLOBAL(
			CD_VIA_ADM,
			DS_VIA_ADM,
			IE_SITUACAO,
			DT_ATUALIZACAO,
			DT_ATUALIZACAO_NREC,
			NM_USUARIO,
			NM_USUARIO_NREC
		) VALUES (
			cd_via_adm_new_w,
			ds_via_adm_p,
			ie_situation_p,
			current_setting('importer_kairos_tools.dt_atual_s')::W_KAIROS.DT_IMPORTACAO%type,
			current_setting('importer_kairos_tools.dt_atual_s')::W_KAIROS.DT_IMPORTACAO%type,
			current_setting('importer_kairos_tools.nm_usuario_s')::W_KAIROS.NM_USUARIO%type,
			current_setting('importer_kairos_tools.nm_usuario_s')::W_KAIROS.NM_USUARIO%type
		);
		COMMIT;
	END IF;
END;

$body$
